import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Form, Button, Row, Col } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { getUserDetails, updateUserProfile } from "../actions/userActions";

function ProfileScreen() {

    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [message, setMessage] = useState("");

    const navigate = useNavigate();
    const dispatch = useDispatch();

    const userDetails = useSelector((state) => state.userDetails);
    const { error, loading, user } = userDetails;
    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin;
    const userUpdateProfile = useSelector((state) => state.userUpdateProfile);
    const { userI } = userLogin;

  // useEffect(() => {
  //   if (!userInfo) {
  //     navigate("/login");
  //   } else {
  //     if (!user || !user.firstName) {
  //       dispatch(getUserDetails("profile"));
  //     } else {
  //       console.log(user)
  //       setFirstName(user.firstName);
  //       setLastName(user.lastName);
  //       setEmail(user.email);
  //     }
  //   }
  // }, [dispatch, navigate, userInfo, user]);

 const [fetchedUserDetails, setFetchedUserDetails] = useState(false);


useEffect(() => {
  if (!userInfo) {
    navigate("/login");
  } else {
    if (!fetchedUserDetails) {
      dispatch(getUserDetails("profile"));
      setFetchedUserDetails(true);
    } else if (user) {
      console.log(user); // Log the user data
      setFirstName(user.first_name);
      setLastName(user.last_name);
      setEmail(user.email);
    }
  }
}, [dispatch, navigate, userInfo, user, fetchedUserDetails]);



    const submitHandler = (e) => {
      e.preventDefault();

      if (password !== confirmPassword) {
        setMessage("Passwords do not match");
      } else {
        dispatch(updateUserProfile({
          'id': user._id,
          'firstName': firstName, //might have to be user.first_name
          'lastName': lastName,
          'email': email,
          'password': password
      }))
      }
    };

  return (
    <Row>
      <Col md={3}>
        <h2>User Profile</h2>
        {message && <Message variant="danger">{message}</Message>}
        {error && <Message variant="danger">{error}</Message>}
        {loading && <Loader />}

        <Form onSubmit={submitHandler}>
          <Form.Group controlId="firstName">
            <Form.Label>First Name</Form.Label>
            <Form.Control
              required
              type="text"
              placeholder="Enter First Name"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group controlId="lastName">
            <Form.Label>Last Name</Form.Label>
            <Form.Control
              required
              type="text"
              placeholder="Enter Last Name"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group controlId="email">
            <Form.Label>Email Address</Form.Label>
            <Form.Control
              required
              type="email"
              placeholder="Enter Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group controlId="password">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Enter Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Form.Group controlId="confirmPassword">
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Confirm Password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
            ></Form.Control>
          </Form.Group>

          <Button className="mt-3" type="submit" variant="primary">
            Update
          </Button>
        </Form>
      </Col>

      <Col md={9}>
        <h2>My Uploads</h2>
      </Col>
    </Row>
  );
}

export default ProfileScreen;
