import {
  PERSON_LIST_REQUEST,
  PERSON_LIST_SUCCESS,
  PERSON_LIST_FAIL,
} from "../constants/personConstants";

const personListReducer = (state = { missingPersons: [] }, action) => {
  switch (action.type) {
    case "PERSON_LIST_REQUEST":
      return { loading: true, missingPersons: [] };

    case "PERSON_LIST_SUCCESS":
      return { loading: false, missingPersons: action.payload.missingPersons };

    case "PERSON_LIST_FAIL":
      return { loading: false, error: action.payload };

    default:
      return state;
  }
};



export default personListReducer;
