import {
  PERSON_LIST_REQUEST,
  PERSON_LIST_SUCCESS,
  PERSON_LIST_FAIL,
} from "../constants/personConstants";

export const personListReducer = (
  state = { personList: [], loading: false, error: "" },
  action
) => {
  switch (action.type) {
    case PERSON_LIST_REQUEST:
      return { ...state, loading: true, personList: [] };

    case PERSON_LIST_SUCCESS:
      return { ...state, loading: false, personList: action.payload };

    case PERSON_LIST_FAIL:
      return { ...state, loading: false, error: action.payload };

    default:
      return state;
  }
};


export default personListReducer;
