import './App.css';
import React, { useState, useEffect } from 'react';
import {  } from 'react'
import axios from 'axios';
import '../src/styles/main.css'
//import MainNavBar from './components/mainNavbar';
import Card from './components/card';
import { person } from './components/person';
import Pagination from './components/pagination';
import Footer from './components/Footer'
import BootstrapHeader from './components/BootstrapHeader';


//const root = document.getElementById('root')
//const searchForm = document.getElementById('search-form')
//const searchBtn = document.getElementById('search-btn') 

function idSearch (data){
  return data.map((item)=>{return item.id})
}

function App() {

  const [query, setQuery] = useState("");
  const [people, setPeople] = useState([])
  const [currentPage, setCurrentPage] = useState(1)
  const [postPerPage, setPostPerPage] = useState(8)


  const search = (data)=>{ 

    return data.filter( item=>{return item.name.toLowerCase().includes(query)})

  }

  const lastPostIndex = currentPage * postPerPage;
  const firstPostIndex = lastPostIndex - postPerPage;
  const currentPosts = search(person).slice(firstPostIndex, lastPostIndex)


  return (
    <>
      <BootstrapHeader />

      <section class="content">
        <div class="main-title-container">
          <h1 class="main-title">Personas desaparecidas en RD 🇩🇴</h1>
        </div>

        <nav className="mainNavbar navbar bg-body-tertiary">
          <div className="maincontainer container-fluid">
            <form
              className="d-flex search-form-container"
              role="search"
              onSubmit={(e) => {
                const root = document.getElementById("search-form").value;
                setQuery(root.toLowerCase());
                console.log(root);
              }}
            >
              <input
                className="search-form form-control me-2"
                id="search-form"
                type="search"
                placeholder="Buscar"
                aria-label="Search"
              />
              <button
                className="btn-search btn btn-outline-success"
                id="search-btn"
                type="submit"
              >
                Buscar
              </button>

              <div className="btn-group">
                <button
                  type="button"
                  className="btn btn-danger dropdown-toggle"
                  data-bs-toggle="dropdown"
                  aria-expanded="false"
                >
                  Filtrar
                </button>
                <ul className="dropdown-menu">
                  <li>
                    <a className="dropdown-item" href="/">
                      Action
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="/">
                      Another action
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="/">
                      Something else here
                    </a>
                  </li>
                  <li>
                    <a className="dropdown-item" href="/">
                      Separated link
                    </a>
                  </li>
                </ul>
              </div>
            </form>
          </div>
        </nav>

        <h3 class="text-left">Búsquedas: </h3>

        <div class=" card-container">
          <Card key={idSearch(person)} data={search(currentPosts)} />
        </div>

        <section class="pagination-container">
          <nav aria-label="...">
            <ul class="pagination">
              <Pagination
                totalPost={search(person).length}
                postPerPage={postPerPage}
                setCurrentPage={setCurrentPage}
                currentPage={currentPage}
              />
            </ul>
          </nav>
        </section>
      </section>

      <Footer />
    </>
  );
}


export default App;
